import React, { Component } from 'react';
import axios from "axios";
import { Col, Row } from 'react-bootstrap';

export class Timeline extends Component {
    constructor(props, context) {
        super(props, context);
        this.state = {
            error: null,
            isLoaded: false,
            timelineEntries: []
        };
    }

    componentDidMount() {
        axios.all([
            axios.get('beckett-timeline.json')])
            .then(axios.spread((getLetterData) => {
                console.log(getLetterData);
                const timelineEntries = getLetterData.data;
                console.log(timelineEntries);
                this.setState({ timelineEntries });
                this.setState({ isLoaded: true })
            }))
            .catch((err) => {
                this.setState({ isLoaded: false });
                this.setState({ error: err.message });
            });
    }

    render() {
        const { error, isLoaded } = this.state;
        // if there is an error
        if (error) {
            return <div>Error: {error.message}</div>;
            // if not loaded show loading
        } else if (!isLoaded) {
            return <div>Loading...</div>;
            // return now that component has value
        } else {
            const timeline = this.state.timelineEntries.map((timelineData) =>
                <Row>
                    <Col md={6} className='timelineEventHolder'>
                        <h3>{timelineData.year}</h3>
                        <h4>{timelineData.date}</h4>
                        <p>{timelineData.event}</p>
                    </Col>
                </Row >
            )
            return (
                <div>
                    {timeline}
                </div>
            )
        }
    }
}

export default Timeline;
