import React, { Component } from "react";
import axios from "axios";
import BootstrapTable from 'react-bootstrap-table-next';
import filterFactory, { textFilter, numberFilter } from 'react-bootstrap-table2-filter';

// const numberFilter = numberFilter({
//   delay: 500,  // how long will trigger filtering after user typing, default is 500 ms
//   placeholder: 'custom placeholder',  // placeholder for number input
//   withoutEmptyComparatorOption: true,  // dont render empty option for comparator
//   withoutEmptyNumberOption: true,  // dont render empty option for numner select if it is defined
//   comparators: [Comparator.EQ, Comparator.GT, Comparator.LT],  // Custom the comparators
//   style: { display: 'inline-grid' },  // custom the style on number filter
//   className: 'custom-numberfilter-class',  // custom the class on number filter
//   comparatorStyle: { backgroundColor: 'antiquewhite' }, // custom the style on comparator select
//   comparatorClassName: 'custom-comparator-class',  // custom the class on comparator select
//   numberStyle: { backgroundColor: 'cadetblue', margin: '0px' },  // custom the style on number input/select
//   numberClassName: 'custom-number-class',  // custom the class on ber input/select
//   defaultValue: { number: 2103, comparator: Comparator.GT },  // default value
// })

const columns = [{
  dataField: 'ID',
  text: 'ID',
  sort: true,
  filter: textFilter()
}, {
  dataField: 'Code',
  text: 'Code',
  sort: true,
  filter: textFilter()
}, {
  dataField: 'Year',
  text: 'Date',
  sort: true,
  filter: numberFilter()
}, {
  dataField: 'Addressed to (Actual)',
  text: 'Addressed to (Actual)',
  sort: true,
  filter: textFilter()
}, {
  dataField: 'Reg place sent',
  text: 'Reg place sent',
  sort: true,
  filter: textFilter()
}, {
  dataField: 'PrimaryLang',
  text: 'PrimaryLang',
  sort: true,
  filter: textFilter()
}];

export class Letters extends Component {
  constructor(props, context) {
    super(props, context);
    this.state = {
      error: null,
      isLoaded: false,
      letters: []
    };
  }

  componentDidMount() {
    axios.all([
      axios.get('letters_json.json')    ])
    .then(axios.spread((getLetters) => {
      const letters = getLetters.data;
      this.setState({ letters });
      console.log(letters)
    }))
    .catch((err) => {
      this.setState({ isLoaded:false });
      this.setState({ error:err.message });
    });
  }

  render() {
    // const allLetters = this.state.letters.map((letter) => <li>{letter.ID}, {letter.Additional} </li>)
      return (
        <div>
          <h1>Letters</h1>
          <BootstrapTable keyField='id' data={ this.state.letters } columns={ columns } filter={ filterFactory() } />
          {/* {allLetters} */}
        </div>
      )
    }
  }

export default Letters;
